FroalaEditor.DefineIcon('accordion', {NAME: 'cellStyle', SVG_KEY: 'cellStyle'});
FroalaEditor.RegisterCommand('accordion', {
    title: 'Insert accordion',
    focus: true,
    undo: true,
    refreshAfterCallback: true,
    callback: function () {
        this.html.insert('<div class="accordion"><div class="accordionHeader"></div><div class="accordionBody"></div></div>');
    }
});